package Display;

public class DisplayMethods {
    public static String sum0 (int num, int num0) { return num + " + " + num0 + " = "; }
    public static String sub0 (int num, int num0) { return num + " - " + num0 + " = "; }
    public static String multiply0 (int num, int num0) { return num + " X " + num0 + " = "; }
    public static String division0 (int num, int num0) { return num + " / " + num0 + " = "; }
    public static String triangleAreaFirstPattern0 (int num, int num0) { return "0.5 X " + num + " x " + num0 + " = "; }
    public static String triangleAreaSecondPattern0 (int num, int num0, int num1, int num2) { return "(" + num + " X " + num0 + " X " + num1 + ") " + " / " + " 4 " + " X " + num2 + " = "; }
    public static String triangleAreaThirdPattern0 (int num, int num0, int num1, int num2) { return num + " X " + " ( " + num0 + " + " + num1 + " + " + num2 + " ( " + "/2" + " ) " + " ) " + " = "; }
    public static String triangleAreaFourthPattern0 (int num, int num0, int num1) { return "0.5 X " + " ( " + num + " X " + num0 + " ) " + " X " + " sin" + num1 + " = "; }
    public static String triangleAreaFifthPattern0 (int num, int num0, int num1, int num2) { return "2 X " + num + "^2 " + " X " + " sin" + num0 + " X " + " sin" + num1 + " X " + " X " + " sin" + num2 + " = "; }
    public static String parallelogramPerimeter0 (int num, int num0) { return "2 " + " X " + num + " 2 " + " X " + num0 + " = "; }
    public static String trapezoidField0 (int num, int num0, int num1) { return "2 / " + " (" + num + "+" + num0 + ") " + " X " + num1 + " = ";}
    public static String circleField0 (int num) { return "π" + num + "^2 " + " = "; }
    public static String circleCircumference0 (int num) { return "2π " + " X " + num + " = "; }
    public static String circleLengthArc0 (int num, int num0) { return num + " / " + " 360 " + " X " + " 2π " + " X " + num0 + " = "; }
    public static String circleSectorField0 (int num, int num0) { return num + " / " + " 360 " + " X " + " π" + num0 + "^2 " + " = "; }
    public static String circleSegmentArea0 (int num, int num0) { return num + " / " + " 360 " + " X " + " π" + num0 + "^2 " + " - " + num0 + "^2 " + " sin" + num + " / " + "2" + " = "; }
    public static String increase0 (int num) { return num + "^2 " + " = "; }
}
